import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Form = () => {
    const [moviesData, setMoviesData] = useState([])


    useEffect(() => {
        axios.get("https://api.themoviedb.org/3/movie/550?api_key=9be84979192a913865a8b7360fe67621").then((res) => console.log(res.data.original_title))
    }, [])


    return (
        <div className='form-component'>
            <div className='form-container'>
                <form>
                    <input type="text" placeholder="Entrez le titre d'un film" />
                    <input type="submit" value="Recherchez" />
                </form>

                <div className='btn-sort-container'>
                    <div className='btn-sort' id='goodToBad'>Top<span>➜</span></div>
                    <div className='btn-sort' id='badToGood'>Flop<span>➜</span></div>
                </div>
            </div>

            <div className='result'>
                {moviesData.map((movie) => <h2>{movie.original_title}</h2>)}
            </div>
        </div>
    );
};

export default Form;
